import { Component, OnInit } from '@angular/core';
import { WebsocketService } from '../../services/websocket.service';
import { ViewChild, ElementRef } from '@angular/core';


@Component({
  selector: 'app-matrix',
  templateUrl: './matrix.component.html',
  styleUrls: ['./matrix.component.css']
})

export class MatrixComponent implements OnInit {
  @ViewChild('scrollContainer') scrollContainer!: ElementRef;

  rows: number = 60; // Fila para la celda a actualizar
  cols: number = 60; // Columna para la celda a actualizar
  matrix: any[][] = []; // Matriz para almacenar los datos recibidos del servidor
  row: number = 0; // Fila para la celda a actualizar
  col: number = 0; // Columna para la celda a actualizar
  row11: number = 0; // Fila para inundacion 
  row12: number = 0; // Fila  para inundacion
  col11: number = 0; // Columna para inundación
  col12: number = 0; // Columna para inundacion
  val: number = 0; // Valor para la celda a actualizar
  pla: number = 2; // Jugador para la celda a actualizar

  activeCell: { row: number, col: number } | null = null;
  visitedCells: { row: number, col: number }[] = [];
  isEnabled: boolean = true;
  keyDownTimer: any;
  keyDownInterval: number = 40;
  isKeyDown: boolean = false;
  windowWidth: number = window.innerWidth;
  windowHeight: number = window.innerHeight - 56;
  sizebox = 26; // Tamaño de una celda + 1 (para el espacio entre celdas)

  constructor(private websocketService: WebsocketService) { }

  ngOnInit(): void {
    window.addEventListener('keydown', this.handleKeyDown.bind(this));
    window.addEventListener('resize', this.updateWindowSize.bind(this));
    this.setupWebSocket();
    this.getMatrixPlayer();
  }

  ngAfterViewInit(): void {
    //entrega las coordenadas de inicio del juego no importa el sitio
    const { row, col } = this.getInitialPosition(this.rows, this.cols)
    this.setActiveCell(row, col)
    this.setActiveCell(row, col + 1)
    this.setActiveCell(row + 1, col)
    this.setActiveCell(row + 1, col + 1)
  }

  ngOnDestroy() {
    // Eliminar listeners al destruir el componente para evitar fugas de memoria
    window.removeEventListener('keydown', this.handleKeyDown.bind(this));
    window.removeEventListener('resize', this.updateWindowSize.bind(this));
  }

  private setupWebSocket(): void {
    const socket = this.websocketService.getMessage();
    socket.subscribe((message) => {
      this.handleIncomingMessage(message);
    });
  }

  private handleIncomingMessage(message: any): void {
    if (message.action === 'updateMatrix') {
      this.matrix = (message.data.matrix);
    }
    if (message.action === 'updateCell') {
      const { row, column, player } = message.data;
      this.matrix[row][column] = player;
    }
  }


  getMatrixPlayer(): void {
    this.websocketService.sendMessage('getMatrix', {});
  }

  updateCell(row: number, column: number, player: number, value: number): void {
    this.websocketService.sendMessage('updCell', { row, column, player, value });
  }

  updateCellsByPlayer(player: number, value: number | null, row: number, column: number) {
    this.websocketService.sendMessage('updateCellsByPlayer', { player, value, row, column });
  }

  getPlayerColor(player: number | null): any {
    switch (player + '') {
      case '1':
        return 'blue';
      case '2':
        return 'red';
      case '3':
        return 'green';
      case '4':
        return 'gray';
      default:
        return 'lightblue';
    }
  }


  handleKeyDown(event: KeyboardEvent): void {
    event.preventDefault();

    if (this.isKeyDown) {
      return;
    }


    if (this.isEnabled) {
      let { row, col } = this.activeCell || { row: 0, col: 0 };
      switch (event.key) {
        case 'ArrowUp':
          row = Math.max(-1, row - 1);

          break;
        case 'ArrowDown':
          row = Math.min(this.rows, row + 1);
          break;
        case 'ArrowLeft':
          col = Math.max(-1, col - 1);
          break;
        case 'ArrowRight':
          col = Math.min(this.cols, col + 1);
          break;
        default:
          return;
      }
      // valida los límites de la cuadrícula
      if (row >= this.rows || col >= this.cols || row < 0 || col < 0) {
        this.isEnabled = false;
        this.updateCellsByPlayer(this.pla, null, this.rows, this.cols);
      } else {
        //busca las celdas ya visitadas 
        const visitedCell = this.visitedCells.find(cell => cell.row === row && cell.col === col);
        if (visitedCell) {
          this.isEnabled = false;
          this.updateCellsByPlayer(this.pla, null, this.rows, this.cols);
          this.visitedCells = [];
        } else {
          this.visitedCells.push({ row, col }); // Agregar la celda al arreglo
          this.setActiveCell(row, col);
        }
      }
    }
    this.isKeyDown = true;
    this.keyDownTimer = setTimeout(() => {
      this.isKeyDown = false;
    }, this.keyDownInterval);

  }


  handleKeyUp(event: KeyboardEvent) {
    this.isKeyDown = false; // Establecer la variable a false cuando se libera la tecla
    if (!this.scrollContainer || !this.scrollContainer.nativeElement) {
      return;
    }
    clearTimeout(this.keyDownTimer); // Limpiar el temporizador
  }


  setActiveCell(row: number, col: number): void {

    try {
      const { player, value } = this.matrix[row][col]
      if (this.pla === player) {
        const { minRow, maxRow, minCol, maxCol } = this.getBounds(this.visitedCells);
        console.log("Menor fila:", minRow);
        console.log("Mayor fila:", maxRow);
        console.log("Menor columna:", minCol);
        console.log("Mayor columna:", maxCol);
        let matTemp: any[][] = [];
        for (let i = 0; i < maxRow - minRow; i++) {
          matTemp[i] = new Array<number>(maxCol - minCol).fill(0);
        }

        for (let i = 0; i < this.visitedCells.length; i++) {
          const { row, col } = this.visitedCells[i];
          matTemp[row - minRow][col - minCol] = 99;
        }

        console.log('.............................' + matTemp);


        /*        for (let i = 0; i < celClose.length; i++) {
                  const cell = celClose[i];
                  console.log(cell.row + ',' + cell.col)
                  this.updateCell(cell.row,  cell.col, this.pla, 0)
                }
                */
        this.visitedCells = [];
        return
      }
    } catch (err) {
      console.log(err)
    }
    this.updateCell(row, col, this.pla, 0);
    const scrollContainerEl = this.scrollContainer.nativeElement;
    scrollContainerEl.scrollTop = ((this.sizebox) * (this.rows + 16) - this.windowHeight) / this.rows * (row);
    scrollContainerEl.scrollLeft = ((this.sizebox) * (this.cols + 16) - this.windowWidth) / this.cols * (col);
    this.activeCell = { row, col };
  }

  isActiveCell(row: number, col: number): boolean {
    return this.activeCell?.row === row && this.activeCell?.col === col;
  }

  updateWindowSize(): void {
    this.windowWidth = window.innerWidth;
    this.windowHeight = window.innerHeight - 56;
  }

  public getInitialPosition(rows: number, cols: number): { row: number, col: number } {
    const row = Math.floor(Math.random() * (rows - 2)) + 2;
    const col = Math.floor(Math.random() * (cols - 2)) + 2; // Generar un número aleatorio entre 0 y (rows - 1)
    return { row, col }
  }

  async getCellPlayer(row: number, column: number): Promise<any> {
    try {
      const cellPlayer = await this.websocketService.sendMessage('getCellPlayer', { row, column });
      return cellPlayer;
    } catch (error) {
      console.error('Error obteniendo el valor de la celda:', error);
      return null;
    }
  }

  createMatrix(row: number, column: number): void {
    this.websocketService.sendMessage('createMatrix', { row, column });
  }

  dropMatrix(row: number, column: number): void {
    this.websocketService.sendMessage('dropMatrix', {});
  }

  fill(row: number, col: number): void {
    try {
      const { player, value } = this.matrix[row][col]
      console.log('player ' + player + ' pla ' + this.pla)
      if (this.pla === player) {
        return;
      }
      console.log('row ' + player + ' col ' + this.pla)
      this.updateCell(row, col, this.pla, 0)
      this.fill(row + 1, col);
      this.fill(row - 1, col);
      this.fill(row, col + 1);
      this.fill(row, col - 1);
    } catch (err) {
    }


  }

  getBounds(cells: { row: number, col: number }[]): { minRow: number, maxRow: number, minCol: number, maxCol: number } {
    return cells.reduce((acc, cell) => {
      if (cell.row < acc.minRow) acc.minRow = cell.row;
      if (cell.row > acc.maxRow) acc.maxRow = cell.row;
      if (cell.col < acc.minCol) acc.minCol = cell.col;
      if (cell.col > acc.maxCol) acc.maxCol = cell.col;
      return acc;
    }, {
      minRow: cells[0]?.row || 0,
      maxRow: cells[0]?.row || 0,
      minCol: cells[0]?.col || 0,
      maxCol: cells[0]?.col || 0
    });
  }















}
