import { Component, OnInit } from '@angular/core';
import { WebsocketService } from '../../services/websocket.service';

@Component({
  selector: 'app-matrix',
  templateUrl: './matrix.component.html',
  styleUrls: ['./matrix.component.css']
})
export class MatrixComponent implements OnInit {
  // Declarar propiedades y métodos según sea necesario
  matrix: any[][] = []; // Matriz para almacenar los datos recibidos del servidor
  row: number = 0; // Fila para la celda a actualizar
  col: number = 0; // Columna para la celda a actualizar
  val: number = 0; // Valor para la celda a actualizar
  pla: number = 0; // Jugador para la celda a actualizar

  constructor(private websocketService: WebsocketService) { }

  ngOnInit(): void {
    // Configurar la conexión WebSocket al iniciar el componente
    this.setupWebSocket();
    // Solicitar la matriz al servidor al iniciar el componente
    this.getMatrixValue();
  }

  // Método para configurar la conexión WebSocket
  private setupWebSocket(): void {
    const socket = this.websocketService.getMessage();
    socket.subscribe((message) => {
      // Manejar los mensajes entrantes del servidor
      this.handleIncomingMessage(message);
    });
  }

  // Método para manejar los mensajes entrantes del servidor
  private handleIncomingMessage(message: any): void {
    ////console.log('Mensaje del servidor:', message);

    // Actualizar la matriz si se recibe un mensaje de actualización de matriz
    if (message.action === 'updateMatrix') {
      //Actualizar la matriz con los nuevos datos recibidos del servidor
      ////console.log('handle component updateMatrix');
      this.matrix = message.data.matrix;
    }

    // Actualizar celdas de la matriz si se recibe un mensaje de actualización de matriz
    if (message.action === 'updateCell') {
      //Actualizar la matriz con los nuevos datos de la celda recibidos del servidor
      const { row, column, value } = message.data;
      ////console.log('handle component updateCell row' + row);
    }

    // Otros tipos de mensajes pueden ser manejados aquí según las necesidades de la aplicación
  }


  // Método para solicitar la matriz al servidor
  getMatrixValue(): void {
    this.websocketService.sendMessage('getMatrix', {});
  }

  // Método asincrónico para obtener el valor de una celda desde el servidor
  async getCellValue(row: number, column: number): Promise<any> {
    try {
      const cellValue = await this.websocketService.sendMessage('getCellValue', { row, column });
      //console.log('Valor de la celda:', cellValue);
      return cellValue;
    } catch (error) {
      console.error('Error obteniendo el valor de la celda:', error);
      // Aquí puedes manejar el error como desees
      return null;
    }
  }

  // Método para actualizar una celda en la matriz
  updateCell(row: number, column: number, value: number, gamer: number): void {
    // Enviar un mensaje al servidor WebSocket para actualizar la celda
    this.websocketService.sendMessage('updCell', { row, column, value, gamer });
    // Después de actualizar la celda, se vuelve a solicitar la matriz al servidor
    this.getMatrixValue();
  }

  // Método para crear una nueva matriz en el servidor
  createMatrix(row: number, column: number): void {
    // Enviar un mensaje al servidor WebSocket para crear la matriz
    this.websocketService.sendMessage('createMatrix', { row, column });
  }






  // Otros métodos y funciones según sea necesario

  // cambia el color de acuerdocon el jugador que esté jugando
  getCellColor(value: number | null): any {
    ////console.log(' getCelllCOlor                   ' + value)
    switch (value + '') {
      case '1':
        return  'blue' ;
      case '2':
        return  'red' ;
      case '3':
        return  'green' ;
      case '4':
        return  'gray' ;
      default:
        return 'lightblue' ;
    }
  }


  // Variables para almacenar la posición activa
  activeCell: { row: number, col: number } | null = null;

  handleKeyDown(event: KeyboardEvent): void {
    let { row, col } = this.activeCell || { row: 0, col: 0 };

    switch (event.key) {
      case 'ArrowUp':
        row = Math.max(0, row - 1);
        break;
      case 'ArrowDown':
        row = Math.min(this.matrix.length - 1, row + 1);
        break;
      case 'ArrowLeft':
        col = Math.max(0, col - 1);
        break;
      case 'ArrowRight':
        col = Math.min(this.matrix[0].length - 1, col + 1);
        break;
      default:
        return;
    }

    this.setActiveCell(row, col);
  }



  setActiveCell(row: number, col: number): void {
    this.activeCell = { row, col };
  }

  isActiveCell(row: number, col: number): boolean {
    return this.activeCell?.row === row && this.activeCell?.col === col;
  }

}
